import React, { useState } from "react";
import { Button } from "react-bootstrap";
import burberryShirt from "burberryShirt.png";

const CheckoutFlow = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    paymentMethod: "",
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePreviousStep = () => {
    setStep(step - 1);
  };

  return (
    <div>
      {step === 1 && (
        <div>
          <div class="product-detail">
            <div class="find-commerce-title">
            EKD PRINT SUIT
            </div>
            <div class="container">
                <div class="image-section">
                    <img src={burberryShirt}/>
                </div>
                <div class="details-section">
                    <div class="details-price">$1050</div>
                    <div class="details-merchant"><span>Sold by</span>Burberry</div>
                    <div class="details-sizing">Size
                        <div class="sizing-icons">
                            <a href="#">XXS</a>
                            <a href="#">XS</a>
                            <a href="#">S</a>
                            <a href="#">M</a>
                            <a href="#">L</a>
                            <a href="#">XL</a>
                        </div>
                    </div>
                    <div class="details-shipping">Ship to New York from London</div>
                    <div class="buy-btn-div"><Button onClick={handleNextStep}>BUY NOW</Button></div>
                </div>   
            </div>
          </div>
        </div>
      )}
      {step === 2 && (
        <div>
          <h2>Step 2: Shipping Address</h2>
          <div>
            <label htmlFor="address">Address:</label>
            <input
              type="text"
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
            />
          </div>
          <Button onClick={handlePreviousStep}>Back</Button>
          <Button onClick={handleNextStep}>Next</Button>
        </div>
      )}
      {step === 3 && (
        <div>
          <h2>Step 3: Payment Method</h2>
          <div>
            <label htmlFor="paymentMethod">Payment Method:</label>
            <select
              id="paymentMethod"
              name="paymentMethod"
              value={formData.paymentMethod}
              onChange={handleChange}
            >
              <option value="">Select a payment method</option>
              <option value="creditCard">Credit Card</option>
              <option value="debitCard">Debit Card</option>
              <option value="paypal">Paypal</option>
            </select>
          </div>
          <Button onClick={handlePreviousStep}>Back</Button>
          <Button onClick={handleNextStep}>Next</Button>
        </div>
      )}
    </div>
  )
}
export default CheckoutFlow;
